Select cad_pessoa.id_pessoa, cad_pessoa.cpf_pessoa, cad_pessoa.data_nascimento, cad_pessoa.nome_social, cad_pessoa.nome_mae, cad_pessoa.telefone, cad_pessoa.status_cadastro, cad_pessoa.foto, Coalesce(cad_pessoa.nome_social, fr_usuario.usr_nome) As usr_nome, fr_usuario.usr_email, fr_usuario.id_pessoa As pes, cad_pessoa.id_genero, cad_pessoa.rendamensal_declarada, cad_pessoa.termos_contrato, cad_pessoa.id_estadocivil, cad_pessoa.cpf_conjuge, cad_pessoa.identificacao_enviado, cad_pessoa.renda_enviado, cad_pessoa.residencia_enviado, cad_pessoa.logradouro, cad_pessoa.complemento, cad_pessoa.cep, cad_pessoa.bairro, cad_pessoa.cidade, cad_pessoa.uf, Regexp_Replace(cad_pessoa.cpf_pessoa, '(\d{3})(\d{3})(\d{3})', '\1.\2.\3-') As cpfformatado, cad_estadocivil.estado_civil, cad_genero.nome_genero, To_Char(cad_pessoa.rendamensal_declarada, 'L9G999G990D99') As rendaformatada, Regexp_Replace(cad_pessoa.cep, '([0-9]{5})([0-9]{3})', '\1-\2') As cepformatado, Regexp_Replace(cad_pessoa.cpf_conjuge, '(\d{3})(\d{3})(\d{3})', '\1.\2.\3-') As cpfconjugeformatado, cad_pessoa.status_cadastro As status, cad_pessoa.numero From cad_pessoa Inner Join fr_usuario On cad_pessoa.id_pessoa = fr_usuario.id_pessoa Left Join cad_estadocivil On cad_pessoa.id_estadocivil = cad_estadocivil.id_estadocivil Left Join cad_genero On cad_pessoa.id_genero = cad_genero.id_genero
/*WHERE_NEW*/ Order By cad_pessoa.id_pessoa
/*ORDER_ADD*/
